import django_filtersfrom django.db.models import Qfrom .models import *class IdcFilter(django_filters.FilterSet):    keywords = django_filters.CharFilter(method="search_keyword")    def search_keyword(self, queryset, name, value):        return queryset.filter(Q(name__icontains=value) | Q(letter__icontains=value) | Q(email__icontains=value) | Q(            phone__icontains=value))    class Meta:        model = Idc        fields = ["keywords"]class ServerFilter(django_filters.FilterSet):    keywords = django_filters.CharFilter(method="search_keyword")    idc = django_filters.NumberFilter(method="search_idc")    product = django_filters.NumberFilter(method="search_product")    service = django_filters.NumberFilter(method="search_service")    def search_keyword(self, queryset, name, value):        return queryset.filter(Q(hostname__icontains=value) | Q(ip__icontains=value))    def search_idc(self, queryset, name, value):        return queryset.filter(idc_id__exact=value)    def search_product(self, queryset, name, value):        return queryset.filter(product_id__exact=value)    def search_service(self, queryset, name, value):        return queryset.filter(service_id__exact=value)    class Meta:        model = Server        fields = ["keywords", "idc", "product", "service"]class ProductFilter(django_filters.FilterSet):    pid = django_filters.CharFilter(method="search_pid")    def search_pid(self, queryset, name, value):        return queryset.filter(pid__exact=value)    class Meta:        model = Product        fields = ["pid"]