from rest_framework.routers import DefaultRouterfrom .views import *cmdb_router = DefaultRouter()cmdb_router.register(r'idcs', IdcViewSet, base_name="idcs")cmdb_router.register(r"product", ProductViewSet, base_name="product")cmdb_router.register(r'server', ServerViewSet, base_name="server")cmdb_router.register(r'idc_product', IdcProductViewSet, base_name="idc_product")cmdb_router.register(r'product_exact', ProductListViewSet, base_name="product_exact")cmdb_router.register(r'service_exact', ServiceListViewSet, base_name="service_exact")cmdb_router.register(r'product_service', ProductServiceViewSet, base_name="product_service")cmdb_router.register(r'service_tree', ServiceTreeViewSet, base_name="service_tree")