from django.conf.urls import include, urlfrom rest_framework.routers import DefaultRouter, SimpleRouterfrom .views import *cmdb_router = DefaultRouter()cmdb_router.register(r'idcs', IdcViewSet, base_name="idcs")cmdb_router.register(r"product", ProductViewSet, base_name="product")cmdb_router.register(r'server', ServerViewSet, base_name="server")cmdb_router.register(r'server_update', ServerUpdateViewSet, base_name="server_update")cmdb_router.register(r'idc_product', IdcProductViewSet, base_name="idc_product")# cmdb_router.register(r'product_exact', ProductListViewSet, base_name="product_exact")cmdb_router.register(r'service_ip', ServiceIPViewSet, base_name="service_ip")cmdb_router.register(r'product_service', ProductServiceViewSet, base_name="product_service")cmdb_router.register(r'service_tree', ServiceTreeViewSet, base_name="service_tree")cmdb_router.register(r'^upload/file', UploadFileViewSet, base_name="upload_file")